import React, { Component } from "react";
import PageTemplate from "../../Page_Template/PageTemplate";

import { Header, Form, Select } from "semantic-ui-react";

import { Departments } from "../../../utils/DepartmenListData";

export default class JoiningInformation extends Component {
  state = {
    recruiting_agency: null,
    date_of_appointment: null,
    order_number: "",
    initial_joining_department: null,
    initial_joining_office: "",
    date_of_joining: null,
    designation: "",
    employee_initial_cadre: null,
    employee_type: null,
    employee_category: null,
  };

  agencyOptions = ["JKPSC", "JKSSB", "OTHER"].map((agency) => {
    return { key: agency, text: agency, value: agency };
  });

  handleLocalFormFill = (key, value) => {
    if (value === null) return;
    let form_data = { ...this.state };

    form_data[key] = value;

    this.setState({ ...form_data });
  };

  render() {
    let form = this.state;
    return (
      <PageTemplate>
        <Header as="h2">12. Joining Information </Header>
        <p>Please enter the details of your joining to the service</p>
        <div>
          <Form>
            <Form.Group widths="equal">
              <Form.Field
                required
                selection
                control={Select}
                options={this.agencyOptions}
                selected={form["recruiting_agency"]}
                label={{
                  children: "Recruiting Agency",
                  htmlFor: "form-select-control-agency",
                }}
                placeholder={form["recruiting_agency"] || "Recruiting Agency"}
                onChange={(e, { value }) => {
                  this.handleLocalFormFill(
                    "recruiting_agency",

                    value
                  );
                }}
                searchInput={{ id: "form-select-control-agency" }}
              />

              <Form.Field
                control={Form.Input}
                label="Order No."
                name="order_number"
                required
                onChange={(e) => {
                  this.handleLocalFormFill(
                    "order_number",

                    e.target.value
                  );
                }}
                value={form["order_number"]}
              />

              <Form.Field
                required
                selection
                search
                control={Select}
                options={Departments.map((dept) => {
                  return { value: dept, text: dept, key: dept };
                })}
                selected={form["initial_joining_department"]}
                label={{
                  children: "Initial Joining Department",
                  htmlFor: "form-select-control-joining_department",
                }}
                placeholder={
                  form["joining_department"] || "Initial Joining Dept."
                }
                onChange={(e, { value }) => {
                  this.handleLocalFormFill(
                    "initial_joining_department",

                    value
                  );
                }}
                searchInput={{ id: "form-select-control-joining_department" }}
              />
            </Form.Group>

            <Form.Group widths="equal">
              <Form.Field
                control={Form.Input}
                label="Initial Joining Office."
                name="initial_joining_office"
                required
                onChange={(e) => {
                  this.handleLocalFormFill(
                    "initial_joining_office",

                    e.target.value
                  );
                }}
                value={form["initial_joining_office"]}
              />

              <Form.Input
                type="date"
                label="Date of Joining"
                required
                onChange={(e) =>
                  this.handleFormFill("date_of_joining", e.target.value)
                }
                value={form["date_of_joining"]}
              />

              <Form.Field
                control={Form.Input}
                label="Designation"
                name="designation"
                required
                onChange={(e) => {
                  this.handleLocalFormFill(
                    "designation",

                    e.target.value
                  );
                }}
                value={form["designation"]}
              />
            </Form.Group>

            <Form.Group>
              <Form.Field
                required
                selection
                search
                control={Select}
                options={[
                  "STATE",
                  "DIVISION",
                  "DISTRICT",
                  "NOT APPLICABLE",
                ].map((cadre) => {
                  return { value: cadre, text: cadre, key: cadre };
                })}
                selected={form["initial_cadre"]}
                label={{
                  children: "Employee Initial Cadre",
                  htmlFor: "form-select-control-cadre",
                }}
                placeholder={form["cadre"] || "Employee Initial Cadre"}
                onChange={(e, { value }) => {
                  this.handleLocalFormFill(
                    "initial_joining_department",

                    value
                  );
                }}
                searchInput={{ id: "form-select-control-joining_department" }}
              />

              <Form.Field
                required
                selection
                search
                control={Select}
                options={["PERMANENT", "TEMPORARY"].map((cadre) => {
                  return { value: cadre, text: cadre, key: cadre };
                })}
                selected={form["employee_type"]}
                label={{
                  children: "Employee Initial Cadre",
                  htmlFor: "form-select-control-employee_type",
                }}
                placeholder={form["cadre"] || "Employee Initial Cadre"}
                onChange={(e, { value }) => {
                  this.handleLocalFormFill(
                    "initial_joining_department",

                    value
                  );
                }}
                searchInput={{ id: "form-select-control-employee_type" }}
              />
            </Form.Group>
          </Form>
        </div>
      </PageTemplate>
    );
  }
}
