import React from 'react'

import {Form,Radio} from "semantic-ui-react"

export default function Question({sno,question,answer,details,handleFormFill}) {
    return (
        <div>
            <Form>
            <h4> {sno}. {question}</h4>

            <Form.Group widths="equal">
            <Form.Field label="Whether Govt. accomodation alloted in Jammu/Srinagar" />
          </Form.Group>
          <Form.Group>
            <Form.Field width={4}>
              <Radio
                label="Yes"
                name="questionRadio"
                value="alloted"
                checked={answer === true}
                onChange={() => {
                  handleFormFill(
                    "employee_office_details",
                    "govt_accomodation_alloted",
                    "alloted"
                  );
                }}
              />
            </Form.Field>
            <Form.Field width={4}>
              <Radio
                label="Not Alloted"
                name="accomodationGroup"
                value="not_alloted"
                checked={form["govt_accomodation_alloted"] === "not_alloted"}
                onChange={() => {
                  handleFormFill(
                    "employee_office_details",
                    "govt_accomodation_alloted",
                    "not_alloted"
                  );
                }}
              />
            </Form.Field>
          </Form.Group>

          </Form>
        </div>
    )
}
