import React from "react";
import { Header, Form, Image, Button, Modal, Select } from "semantic-ui-react";
import styles from "./Social.module.css";

import default_img from "../../assets/image.png";

import { Departments as DepartmentList } from "../../utils/DepartmenListData";

export default function General({
  handleFormFill,
  form,
  incrementStep,
  decrementStep,
}) {
  let departmentalOptions = DepartmentList.map((department) => {
    return {
      key: department,
      text: department,
      value: department,
    };
  });

  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");

  function validateInputs(form) {
    console.log(form);

    let validated = true;
    let errorMessgae = "";
    let fields = ["email"];

    fields.forEach((field) => {
      if (form[field] === "" || form[field] === null) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;
      }
    });

    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
      return false;
    }

    return true;
  }
  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          7. Electronic Communication Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Email"
              name="email"
              required
              onChange={(e) => {
                handleFormFill("social_information", "email", e.target.value);
              }}
              value={form["email"]}
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Facebook"
              name="fb"
              onChange={(e) => {
                handleFormFill("social_information", "fb", e.target.value);
              }}
              value={form["fb"]}
            />

            <Form.Field
              control={Form.Input}
              label="Twitter"
              name="twitter"
              onChange={(e) => {
                handleFormFill("social_information", "twitter", e.target.value);
              }}
              value={form["twitter"]}
            />

            <Form.Field
              control={Form.Input}
              label="WhatsApp"
              name="whatsapp"
              onChange={(e) => {
                handleFormFill(
                  "social_information",
                  "whatsapp",
                  e.target.value
                );
              }}
              value={form["whatsapp"]}
            />
          </Form.Group>

          <div className={styles["btn_wrapper"]}>
            <Button.Group>
              <Button
                size="medium"
                color="yellow"
                onClick={(e) => {
                  e.preventDefault();
                  decrementStep();
                }}
              >
                BACK: (5). EDUCATIONAL INFO
              </Button>
              <Button.Or />
              <Button
                size="medium"
                positive
                disabled={!this.validatedMainSubmission()}
                onClick={(e) => {
                  e.preventDefault();

                  if (this.validatedMainSubmission()) {
                    // handleArrayFill("mobile_numbers", [
                    //   ...this.state.mobile_numbers,
                    // ]);
                  } else return false;
                }}
              >
                (6). e-Communication : NEXT
              </Button>
            </Button.Group>
          </div>
        </Form>
      </div>
    </div>
  );
}
