import React from "react";
import { Header, Form, Image, Button, Modal } from "semantic-ui-react";
import styles from "./Domicile.module.css";

import default_img from "../../assets/image.png";

export default function DomicileInfo({ handleFormFill, form, incrementStep }) {
  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");

  function validateInputs(form) {
    console.log(form);

    let validated = true;
    let errorMessgae = "";
    let fields = [
      "name",
      "present_department",
      "parent_department",
      "designation",
      "employee_id",
    ];

    fields.forEach((field) => {
      if (form[field] === "" || form[field] === undefined) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;
      }
    });

    if (form["photo"] === null) {
      validated = false;
      errorMessgae = `Please upload a proper photograph`;
    }

    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
      return false;
    }

    return true;
  }
  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          4. Domicile Related Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Domicile Certificate No."
              name="certificate_no"
              required
              onChange={(e) => {
                handleFormFill(
                  "domicile_information",
                  "certificate_no",
                  e.target.value
                );
              }}
              value={form["certificate_no"]}
            />
            <Form.Field
              control={Form.Input}
              label="Issuing Authority"
              name="issuing_authority"
              required
              onChange={(e) => {
                handleFormFill(
                  "domicile_information",
                  "issuing_authority",
                  e.target.value
                );
              }}
              value={form["issuing_authority"]}
            />
          </Form.Group>

          <Form.Group>
            <Form.Input
              required
              type="file"
              accept=".jpg,jpeg,.png"
              label="Uplaod Photograph"
              onChange={(e) => {
                let file_type = e.target.files[0].type;

                if (!file_type.includes("image")) {
                  e.target.value = "";
                  return;
                }

                handleFormFill(
                  "general_information",
                  "photo",
                  e.target.files[0]
                );
              }}
            />

            <p>Affix signed Passport size (5cm x 7cm) recent Photograph</p>

            <div>
              <Image
                src={
                  form["photo"] === null
                    ? default_img
                    : window.URL.createObjectURL(form["photo"])
                }
                size="medium"
              />
              <p>Selected Photograph</p>
            </div>
          </Form.Group>

          <div className={styles["btn_wrapper"]}>
            <Button
              size="big"
              content="NEXT : Personal Details"
              onClick={(e) => {
                e.preventDefault();
                if (validateInputs(form)) {
                  incrementStep();
                }
              }}
              color="green"
            />
          </div>
        </Form>
      </div>
    </div>
  );
}
