import React from "react";
import { Header, Form, Button, Modal, Select } from "semantic-ui-react";
import styles from "./Address.module.css";

export default function General({ handleFormFill, form ,incrementStep , decrementStep }) {
  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");


  let districtOptions = [
    { key: "kathua", text: "Kathua", value: "kathua" },
    { key: "jammu", text: "Jammu", value: "jammu" },
    { key: "samba", text: "Samba", value: "samba" },
    { key: "udhampur", text: "Udhampur", value: "udhampur" },
    { key: "reasi", text: "Reasi", value: "reasi" },
    { key: "rajouri", text: "Rajouri", value: "rajouri" },
    { key: "poonch", text: "Poonch", value: "poonch" },
    {key:"doda",text:"Doda",value:"doda"},
    {key:"ramban",text:"Ramban",value:"Ramban"},
    {key:"kishtwar",text:"Doda",value:"kishtwar"},
    {key:"anantnag",text:"Anantnag",value:"anantnag"},
    {key:"kulgam",text:"Kulgam",value:"kulgam"},
    {key:"pulwama",text:"Pulwama",value:"pulwama"},
    {key:"shopian",text:"Shopian",value:"shopian"},
    {key:"budgam",text:"Budgam",value:"budgam"},
    {key:"Srinagar",text:"Srinagar",value:"srinagar"},
    {key:"Ganderbal",text:"Ganderbal",value:"ganderbal"},
     {key:"Bandipora",text:"Bandipora",value:"bandipora"},
     {key:"Baramulla",text:"Baramulla",value:"baramulla"},
     {key:"Kupwara",text:"Kupwara",value:"kupwara"},
     {key:"outside",text:"Outise JK UT",value:"NA"},




  ]

  let states_ut = [

  ]


  function validateInputs(form) {
    console.log(form);

    let validated = true;
    let errorMessgae = "";
    let fields = ["first_name", "last_name", "middle_name", "sex", "category","blood_group"];

    fields.forEach((field) => {
      if (form[field] === "" || form[field] === undefined || form[field] === null) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;
      }
    });



    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
      return false
    }

    return true;
  }



  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          3. Address Related Information
        </Header>
        <Form>
            <Header as="h4">3(i) Present Address</Header>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="House No./Name"
              name="house"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "first_name",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["first_name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Locality / Street"
              name="locality"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "locality",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["locality"]}
            />
            <Form.Field
              control={Form.Input}
              label="Town/Village"
              name="house"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "town_village",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["town_village"]}
            />
          </Form.Group>


          <Form.Group widths="equal">
          <Form.Field
              control={Form.Input}
              label="Police Station"
              name="police_station"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "police_station",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["police_station"]}
            />
             <Form.Field
              control={Form.Input}
              label="Post Office"
              name="post_office"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "post_office",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["post_office"]}
            />
          </Form.Group>

          <Form.Group widths="equal">
          <Form.Field
              required
              control={Select}
              options={districtOptions}
              label={{
                children: "District",
                htmlFor: "form-select-control-district",
              }}
              placeholder="District"
              onChange={(e, { value }) => {
                handleFormFill("address_information", "sex", value,"district");
              }}
              searchInput={{ id: "form-select-control-district" }}
            />
             <Form.Field
              control={Form.Input}
              label="Post Office"
              name="post_office"
              required
              onChange={(e) => {
                handleFormFill(
                  "address_information",
                  "post_office",
                  e.target.value,
                  "present_address"
                );
              }}
              value={form["present_address"]["post_office"]}
            />
          </Form.Group>
          <div className={styles["btn_wrapper"]}>
            <Button.Group>
              <Button size="large" color="yellow" onClick={e=>{
                  decrementStep()
              }}>
                BACK: 1. GENERAL INFO
              </Button>
              <Button.Or />
              <Button size="large" positive onClick={e=>{
                  e.preventDefault()

                  //CHECK VALIDATION BEFORE SUBMIT

                  if(validateInputs(form)){
                      incrementStep()
                  }
              }}>
                 3. ADDRESS INFO : NEXT
              </Button>
            </Button.Group>
          </div>
        </Form>
      </div>
    </div>
  );
}
