import React, { Component } from "react";
import PageTemplate from "../../Page_Template/PageTemplate";

import { Header, Form, Select } from "semantic-ui-react";

export default class JoiningInformation extends Component {
  state = {
    recruiting_agency: "",
    date_of_appointment: null,
    order_number: "",
    initial_joining_department: null,
    initial_joining_office: "",
    date_of_joining: null,
    designation: "",
    employee_initial_cadre: null,
    employee_type: null,
    employee_category: null,
  };

  render() {
    let form = this.state;
    return (
      <PageTemplate>
        <Header as="h2">12. Joining Information </Header>
        <p>Please enter the details of your joining to the service</p>
        <div>
          <Form>
            <Form.Group>
              <Form.Field
                required
                selection
                control={Select}
                options={districtOptions}
                selected={form["permanent_address"]["district"]}
                label={{
                  children: "District",
                  htmlFor: "form-select-control-district",
                }}
                placeholder={
                  form["permanent_address"]["district"] || "District"
                }
                onChange={(e, { value }) => {
                  handleFormFill(
                    "address_information",
                    "district",
                    value,
                    "permanent_address"
                  );
                }}
                searchInput={{ id: "form-select-control-district" }}
              />
            </Form.Group>
          </Form>
        </div>
      </PageTemplate>
    );
  }
}
