import React from 'react'

import {Form,Radio} from "semantic-ui-react"

export default function Question({sno,question,answer,details,handleFormFill}) {
    return (
        <div style={{paddingTop:"3rem"}}>
            <Form>
            <h4> {sno}. {question}</h4>


          <Form.Group>
            <Form.Field width={4}>
              <Radio
                label="Yes"
                name="questionRadio"
                value="Yes"
                checked={answer === true}
                onChange={() => {
                 handleFormFill(sno,"answer",true)
                }}
              />
            </Form.Field>
            <Form.Field width={4}>
            <Radio
                label="No"
                name="questionRadio"
                value="No"
                checked={answer === false}
                onChange={() => {
                 handleFormFill(sno,"answer",false)
                }}
              />
            </Form.Field>
          </Form.Group>

          </Form>
        </div>
    )
}
