import React from "react";
import { Header, Form, Button, Modal, Select } from "semantic-ui-react";
import styles from "./Personal.module.css";

export default function General({ handleFormFill, form }) {
  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");

  function validateInputs(form) {
    console.log(form);

    let validated = true;
    let errorMessgae = "";
    let fields = ["name", "present_department", "designation", "employee_id"];

    fields.forEach((field) => {
      if (form[field] === "" || form[field] === undefined) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;
      }
    });

    if (form["photo"] === null) {
      validated = false;
      errorMessgae = `Please upload a proper photograph`;
    }

    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
    }

    return true;
  }

  const genderOptions = [
    { key: "m", text: "Male", value: "male" },
    { key: "f", text: "Female", value: "female" },
    { key: "o", text: "Other", value: "other" },
  ];

  const bloodGroupOptions = [
    { key: "A+", text: "A RhD positive (A+)", value: "A+" },
    { key: "A-", text: "A RhD negative (A-)", value: "A-" },
    { key: "B+", text: "B RhD positive(B+)", value: "B+" },
    { key: "B-", text: "B RhD negative (B-)", value: "B-" },
    { key: "O-", text: "O RhD negative (O-)", value: "O-" },
    { key: "O-", text: "O RhD negative (O-)", value: "O-" },
    { key: "AB-", text: "AB RhD negative (AB-)", value: "AB-" },
    { key: "AB-", text: "AB RhD negative (AB-)", value: "AB-" }
  ];

  const categoryOptions = [
      {key:"om" ,text:"OPEN MERIT ",value:"om"},
      {key:"sc" ,text:"SC	SCHEDULED CASTES",value:"sc"},
      {key:"stgp" ,text:"SCHEDULED TRIBE, GUJJAR AND BAKERWAL",value:"stgp"},
      {key:"osc" ,text:"WEAK AND UNDER PRIVILEGED CLASSES/SOCIAL CASTES",value:"osc"},
      {key:"rba" ,text:"RESIDENTS OF BACKWARD AREA",value:"rba"},
      {key:"alc" ,text:"RESIDENTS OF AREA ADJOINING ACTUAL LINE OF CONTROL ",value:"alc"},
      {key:"sp" ,text:"PROFICIENCY IN SPORTS",value:"sp"},
      {key:"ews" ,text:"ECONOMICALLY WEAKER SECTION",value:"ews"},
  ]

  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          2. Personal Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="First Name"
              name="first_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "first_name",
                  e.target.value
                );
              }}
              value={form["first_name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Middle Name"
              name="middle_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "middle_name",
                  e.target.value
                );
              }}
              value={form["middle_name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Last Name"
              name="last_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "last_name",
                  e.target.value
                );
              }}
              value={form["last_name"]}
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
             required
              control={Select}
              options={genderOptions}
              label={{
                children: "Gender / Sex",
                htmlFor: "form-select-control-gender",
              }}
              placeholder="Gender"
              onChange={(e, { value }) => {
                handleFormFill("personal_information", "sex", value);
              }}
              searchInput={{ id: "form-select-control-gender" }}
            />
           <Form.Field
           required
              control={Select}
              options={categoryOptions}
              label={{
                children: "Category",
                htmlFor: "form-select-control-category",
              }}
              placeholder="Category"
              search
              onChange={(e, { value }) => {
                handleFormFill("personal_information", "category", value);
              }}
              searchInput={{ id: "form-select-control-category" }}
            />

            <Form.Field
             required
              control={Select}
              options={bloodGroupOptions}
              label={{
                children: "Blood Group",
                htmlFor: "form-select-control-blood",
              }}
              placeholder="Blood Group"
              onChange={(e, { value }) => {
                handleFormFill("personal_information", "blood_group", value);
              }}
              searchInput={{ id: "form-select-control-blood" }}
            />
          </Form.Group>



          <Form.Group widths="equal">
          <Form.Field
              control={Form.Input}
              label="Alias Name"
              name="alias_name"

              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "alias",
                  e.target.value
                );
              }}
              value={form["alias"]}
            />

<Form.Field
              control={Form.Input}
              label="Birth Name"
              name="birth_name"

              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "alias",
                  e.target.value
                );
              }}
              value={form["birth_name"]}
            />
          </Form.Group>
          <div className={styles["btn_wrapper"]}>
            <Button
              size="big"
              content="NEXT : Personal Details"
              onClick={() => {
                validateInputs(form);
              }}
              color="green"
            />
          </div>
        </Form>
      </div>
    </div>
  );
}
