import React, { Component } from "react";

import styles from "./Educational.module.css";

import Modal from "../../utils/Modal/Modal";

import { Header, Form, Button } from "semantic-ui-react";

import Table from "./MobileTable/Table";

export default class EducationInfo extends Component {
  state = {
    modalOpen: false,
    counter: 0,
    mobile_numbers:[],
    current_mobile_number:""
  };

  // validatedMainSubmission = () => {
  //   let educational_qualifications_data = this.state.educational_qualifications;
  //   if (educational_qualifications_data.length > 0) {
  //     return true;
  //   } else return false;
  // };

  validated = () => {
    if(this.state.current_mobile_number === "" || this.state.current_mobile_number.length<10){
      return false
    }
    else return true
  };

  clearExistingForm = () => {
    let current_educational_form = { ...this.state.education };
    let fields = Object.keys(current_educational_form);
    fields.forEach((field) => {
      current_educational_form[field] = "";
    });
    this.setState({
      education: current_educational_form,
    });
  };

  storeCurrentInArray = () => {
    let counter = this.state.counter;
    let current_educational_form = { ...this.state.education, id: counter };
    console.log(current_educational_form, "current_ed_form");

    let educational_qualifications_data = [
      ...this.state.educational_qualifications,
      current_educational_form,
    ];

    this.setState({
      educational_qualifications: educational_qualifications_data,
      counter: counter + 1,
      modalOpen: false,
    });
  };

  removeItemFormTable = (value) => {
    let educational_qualifications_data = [
      ...this.state.educational_qualifications,
    ];
    educational_qualifications_data = educational_qualifications_data.filter(
      (item) => {
        console.log(item["id"], value);
        return item["id"] !== value;
      }
    );

    this.setState({
      educational_qualifications: educational_qualifications_data,
    });
  };

  handleLocalMobileFill = (value) =>{
      this.setState({current_mobile_number:value})
  }

  handleAddMobile = (mobile_number) =>{
    let local_mobile_numbers = [...this.state.mobile_numbers,mobile_number]

    this.setState({mobile_numbers:[...local_mobile_numbers]})

  }
  changeModal = (state) => {
    let modal = state === "open" ? true : false;
    this.setState({ modalOpen: modal });
  };
  render() {
    let { incrementStep, decrementStep, handleArrayFill } = {
      ...this.props,
    };

    let form = this.state.education;

    return (
      <div className="container">
        <div className={styles["main_wrapper"]}>
          <Modal
            modalState={this.state.modalOpen}
            modalMessage="Add new Mobile Number"
            closeModal={() => this.changeModal("close")}
          >
            <Form>
              <Form.Group widths="equal">
                <Form.Field
                  control={Form.Input}
                  label="Mobile Number"
                  name="mobile_number_local"
                  required
                  onChange={(e) => {
                   this.handleLocalMobileFill(e.target.value)
                  }}
                  value={this.state["current_mobile_number"]}
                />
              </Form.Group>


              <Form.Group>
                <Button
                  positive
                  onClick={(e) => {
                    if (this.validated()) {
                      this.storeCurrentInArray();
                      this.clearExistingForm();
                    }
                  }}
                >
                  Add Number to List
                </Button>
              </Form.Group>
            </Form>
          </Modal>

          <Header as="h2">6. Mobile Numbers</Header>
          <p>
            Please fill up the details of Mobile Numbers used during last 5 years.
          </p>

          <Button
            positive
            onClick={(e) => {
              this.setState({
                modalOpen: true,
              });
            }}
          >
            Add Educational Detail
          </Button>
        </div>
        <Table
          removeItemFormTable={this.removeItemFormTable}
          data={this.state.educational_qualifications}
        />

        <div className={styles["btn_wrapper"]}>
          <Button.Group>
            <Button
              size="medium"
              color="yellow"
              onClick={(e) => {
                e.preventDefault();
                decrementStep();
              }}
            >
              BACK: (4). DOMICILE INFO
            </Button>
            <Button.Or />
            <Button
              size="medium"
              positive
              disabled={!this.validatedMainSubmission()}
              onClick={(e) => {
                e.preventDefault();

                if (this.validatedMainSubmission()) {
                  incrementStep();
                } else return false;
              }}
            >
              (6). MOBILE DETAILS : NEXT
            </Button>
          </Button.Group>
        </div>
      </div>
    );
  }
}
