import React from "react";
import { Header, Form, Button, Modal, Select } from "semantic-ui-react";
import styles from "./Personal.module.css";

export default function General({ handleFormFill, form }) {
  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");

  function validateInputs(form) {
    console.log(form);

    let validated = true;
    let errorMessgae = "";
    let fields = ["name", "present_department", "designation", "employee_id"];

    fields.forEach((field) => {
      if (form[field] === "" || form[field] === undefined) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;
      }
    });

    if (form["photo"] === null) {
      validated = false;
      errorMessgae = `Please upload a proper photograph`;
    }

    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
    }

    return true;
  }

  const genderOptions = [
    { key: "m", text: "Male", value: "male" },
    { key: "f", text: "Female", value: "female" },
    { key: "o", text: "Other", value: "other" },
  ];

  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          2. Personal Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="First Name"
              name="first_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "first_name",
                  e.target.value
                );
              }}
              value={form["name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Middle Name"
              name="middle_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "middle_name",
                  e.target.value
                );
              }}
              value={form["name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Last Name"
              name="last_name"
              required
              onChange={(e) => {
                handleFormFill(
                  "personal_information",
                  "last_name",
                  e.target.value
                );
              }}
              value={form["name"]}
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
              control={Select}
              options={genderOptions}
              label={{
                children: "Gender / Sex",
                htmlFor: "form-select-control-gender",
              }}
              placeholder="Gender"
              onChange={(e, { value }) => {
                handleFormFill("personal_information", "last_name", value);
              }}
              searchInput={{ id: "form-select-control-gender" }}
            />
            <Form.Field
              control={Form.Input}
              label="Employee ID"
              name="employee_id"
              required
              onChange={(e) => {
                handleFormFill(
                  "general_information",
                  "employee_id",
                  e.target.value
                );
              }}
              value={form["employee_id"]}
            />
          </Form.Group>

          <div className={styles["btn_wrapper"]}>
            <Button
              size="big"
              content="NEXT : Personal Details"
              onClick={() => {
                validateInputs(form);
              }}
              color="green"
            />
          </div>
        </Form>
      </div>
    </div>
  );
}
