import React from "react";
import { Header, Form, Image, Button, Modal } from "semantic-ui-react";
import styles from "./Personal.module.css"


export default function General({ handleFormFill, form }) {
  const [open, setOpen] = React.useState(false);
  const [modalMessage, setModalMessgae] = React.useState("");

  function validateInputs(form) {
    console.log(form);


    let validated = true;
    let errorMessgae = "";
    let fields = ["name", "present_department", "designation", "employee_id"];

    fields.forEach((field) => {
      if (
        form[field] === "" ||
        form[field] === undefined
      ) {
        validated = false;
        errorMessgae = `Invalid Input field: ${field}`;

      }
    });

    if (form["photo"] === null) {
      validated = false;
      errorMessgae = `Please upload a proper photograph`;
    }

    if (!validated) {
      setModalMessgae(errorMessgae);
      setOpen(true);
    }

    return true;
  }
  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Modal
          centered={false}
          open={open}
          onClose={() => setOpen(false)}
          onOpen={() => setOpen(true)}
        >
          <Modal.Header>Please Note</Modal.Header>
          <Modal.Content>
            <Modal.Description>{modalMessage}</Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button onClick={() => setOpen(false)}>OK</Button>
          </Modal.Actions>
        </Modal>
        <Header className={styles["heading"]} as="h2">
          General Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Name"
              name="name"
              required
              onChange={(e) => {
                handleFormFill("general_information", "name", e.target.value);
              }}
              value={form["name"]}
            />
            <Form.Field
              control={Form.Input}
              label="Present Department"
              name="present_department"
              required
              onChange={(e) => {
                handleFormFill(
                  "general_information",
                  "present_department",
                  e.target.value
                );
              }}
              value={form["present_department"]}
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Designation"
              name="designation"
              required
              onChange={(e) => {
                handleFormFill(
                  "general_information",
                  "designation",
                  e.target.value
                );
              }}
              value={form["designation"]}
            />
            <Form.Field
              control={Form.Input}
              label="Employee ID"
              name="employee_id"
              required
              onChange={(e) => {
                handleFormFill(
                  "general_information",
                  "employee_id",
                  e.target.value
                );
              }}
              value={form["employee_id"]}
            />
          </Form.Group>



          <div className={styles["btn_wrapper"]}>
            <Button
              size="big"
              content="NEXT : Personal Details"
              onClick={() => {
                validateInputs(form)
              }}
              color="green"
            />
          </div>
        </Form>
      </div>
    </div>
  );
}
