import React from "react";
import { Header, Form, Image, Button } from "semantic-ui-react";
import styles from "./General.module.css";

import default_img from "../../assets/image.png";




export default function General({handleFormFill,form}) {
  function validateInputs(form){
    let validated = true
    let errorMessgae = ""
    let fields = ["name","present_department","designation","employee_id"]

    fields.forEach((field)=>{
      if(form["general_information"][field] === "" || form["general_information"][field] === undefined){
        validated=false
        errorMessgae = `Invalid Input field: ${field}`
      }
    })

    if(form["general_information"]["photo"] === null){
      validated=false
      errorMessgae = `Please upload a proper photograph`
    }

    if(!validated){

    }

  }
  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Header className={styles["heading"]} as="h2">
          General Information
        </Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field control={Form.Input} label="Name" name="name" required onChange={(e)=>{
              handleFormFill("general_information","name",e.target.value)
            }} value={form["name"]} />
            <Form.Field
              control={Form.Input}
              label="Present Department"
              name="present_department"
              required
              onChange={(e)=>{
                handleFormFill("general_information","present_department",e.target.value)
              }} value={form["present_department"]}
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Designation"
              name="designation"
              required
              onChange={(e)=>{
                handleFormFill("general_information","designation",e.target.value)
              }}
              value={form["designation"]}
            />
            <Form.Field
              control={Form.Input}
              label="Employee ID"
              name="employee_id"
              required
              onChange={(e)=>{
                handleFormFill("general_information","employee_id",e.target.value)
              }}
              value={form["employee_id"]}
            />
          </Form.Group>

          <Form.Group>
            <div className={styles["photo_upload_section"]}>
              <div>
                <Form.Input
                required
                  type="file"
                  accept=".jpg,jpeg,.png"
                  label="Uplaod Photograph"
                  onChange={(e) => {

                    let file_type = e.target.files[0].type 

                    if(!file_type.includes("image")){
                      e.target.value="" 
                      return
                    }

                    handleFormFill("general_information","photo",
                      e.target.files[0]
                    );
                  }}
                />

                <p>Affix signed Passport size (5cm x 7cm) recent Photograph</p>
              </div>

              <div>
              <Image src={
                (form["photo"]===null)?default_img:(window.URL).createObjectURL(form["photo"])
              } size="medium"  />
              <p>Selected Photograph</p>
              </div>
            </div>
          </Form.Group>

          <div className={styles["btn_wrapper"]}>
            <Button size="big" content="NEXT : Personal Details" color="green" />
          </div>
        </Form>
      </div>
    </div>
  );
}
