import React from "react";
import { Header, Form,Image } from "semantic-ui-react";
import styles from "./General.module.css";

import default_img from "../../assets/image.png"

export default function General() {
  return (
    <div className="container">
      <div className={styles["main_wrapper"]}>
        <Header as="h3">General Information</Header>
        <Form>
          <Form.Group widths="equal">
            <Form.Field control={Form.Input} label="Name" name="name" />
            <Form.Field
              control={Form.Input}
              label="Present Department"
              name="present_department"
            />
          </Form.Group>
          <Form.Group widths="equal">
            <Form.Field
              control={Form.Input}
              label="Designation"
              name="designation"
            />
            <Form.Field
              control={Form.Input}
              label="Employee ID"
              name="employee_id"
            />
          </Form.Group>

          <Form.Group>


          <div>
          <Form.Input
              type="file"
              label="Uplaod Photograph"
              onChange={(e) => {
                console.log(e.target.files[0]);
                this.handleFormFilling(
                  "download_attachment",
                  e.target.files[0]
                );
              }}
            />

            <p>
                Affix signed Passport size (5cm x 7cm) recent Photograph
            </p>
          </div>



            <Image src={default_img} size="medium" disabled/>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
}
