import React, { Component } from 'react'

import 'semantic-ui-css/semantic.min.css'

import "./index.css"

import GeneralInformation from "../../components/General_Info/General"
import PersonalInformation from "../../components/Personal_Info/PersonalInfo"


export default class App extends Component {

    state = {   

        currentStep:2
        ,

        form:{
            general_information:{
                name:"",
                present_department:"",
                parent_department:"",
                designation:"",
                employee_id:"",
                photo:null
            },
            personal_information:{
                first_name:"",
                middle_name:"",
                last_name:"",
                sex:"",
                alias:"",
                category:"",
                blood_group:"",
                birth_name:""
            }
        }

    }

    handleFormFill = (section,id,value) =>{

        let form = {...this.state.form}

        let section_data = {...this.state.form[section]}

        if(value === undefined || value === ""){
            return
        }

        section_data[id] = value

        form[section] = {...section_data}
        this.setState({form:form})


    }

    incrementStep = () =>{
        let currentStep = this.state.currentStep
        this.setState({currentStep:currentStep+1})
    }

    decrementStep = () =>{
        let currentStep = this.state.currentStep
        this.setState({currentStep:currentStep-1})
    }



    render() {
        switch(this.state.currentStep){
            case 1: return(
                <GeneralInformation form={this.state.form.general_information} handleFormFill = {this.handleFormFill} incrementStep={this.incrementStep}/>
            )

            case 2: return(
                <PersonalInformation form={this.state.form.personal_information} handleFormFill= {this.handleFormFill}/>
            )

            default: return(
                <div>Lost</div>
            )
        }
    }
}
